mapper.select:
select ns.news_id,ns.news_contents,ns.img_name,ns.news_author,
ns.order_id,ns.news_time,nr.receive_uid
from news ns INNER JOIN news_receive nr on ns.news_id=nr.ns_id
where ns.order_id=#{param4} and ns.news_author=#{param3}
    or nr.receive_uid=#{param3} and ns.order_id=#{param4}
ORDER BY ns.news_time desc LIMIT #{param1},#{param2}

select ns.news_contents,oi.buy_user,oi.buy_admin,oi.order_id, (select ui.u_name from user_info ui where ui.u_id=#{param1}) as userName, (select uf.face_file from user_face uf where uf.u_id=#{param1}) as userFace from order_info oi LEFT JOIN news ns on oi.order_id=ns.order_id where
oi.buy_user=#{param1}
oi.buy_admin=#{param1}
and oi.order_state!='已完成' ORDER BY ns.news_time desc LIMIT 0,1
